class NestedIterator {
    private Stack<Integer> stack;
    private Stack<Integer> flatStack;

    public NestedIterator(List<NestedInteger> nestedList) {
        stack = new Stack<>();
        flatStack = new Stack<>();
        flatten(nestedList);
    }

    private void flatten(List<NestedInteger> nestedList) {
        for (NestedInteger nestedInteger : nestedList) {
            if (nestedInteger.isInteger()) {
                flatStack.push(nestedInteger.getInteger());
            } else {
                flatten(nestedInteger.getList());
            }
        }
    }

    public int next() {
        if (!flatStack.isEmpty()) {
            return flatStack.pop();
        } else {
            throw new RuntimeException("No more elements to process");
        }
    }

    public boolean hasNext() {
        while (!stack.isEmpty()) {
            if (!flatStack.isEmpty()) {
                return true;
            }
            NestedInteger nestedInteger = stack.pop();
            if (nestedInteger.isInteger()) {
                flatStack.push(nestedInteger.getInteger());
            } else {
                flatten(nestedInteger.getList());
            }
        }
        return flatStack.isEmpty();
    }
}

