class UnionFind:
    def __init__(self, n):
        # Initialize the parent array with the indices of the nodes as its initial values
        self.parent = list(range(n))
        self.rank = [0] * n

    def find(self, x):
        # Find the root of the node x
        if self.parent[x]!= x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        # Perform union of the nodes x and y
        root_x = self.find(x)
        root_y = self.find(y)
        if root_x!= root_y:
            if self.rank[root_x] < self.rank[root_y]:
                self.parent[root_x] = root_y
            elif self.rank[root_x] > self.rank[root_y]:
                self.parent[root_y] = root_x
            else:
                self.parent[root_y] = root_x
                self.rank[root_x] += 1


def largestComponentSize(nums):
    n = len(nums)
    uf = UnionFind(n)
    primes = set()

    def get_primes(num):
        # Function to generate all prime factors of a number
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                primes.add(i)
                while num % i == 0:
                    num //= i
        if num > 1:
            primes.add(num)

    for i in range(n):
        get_primes(nums[i])
        for prime in primes:
            if nums[i] % prime == 0:
                uf.union(i, nums[i] // prime - 1)

    count = {}
    for i in range(n):
        root = uf.find(i)
        count[root] = count.get(root, 0) + 1

    return max(count.values())


