class Solution:
    def isPossibleToCover(self, grid, stampHeight, stampWidth):
        m, n = len(grid), len(grid[0])
        def is_valid(x, y):
            return 0 <= x < m and 0 <= y < n

        def dfs(i, j):
            if i == m or j == n:
                return True
            if grid[i][j] == 1:
                return False
            for x in range(stampHeight):
                for y in range(stampWidth):
                    if not is_valid(i + x, j + y):
                        return False
                    if grid[i + x][j + y] == 1:
                        return False
            for x in range(stampHeight):
                for y in range(stampWidth):
                    grid[i + x][j + y] = 1
            for x in range(stampHeight):
                for y in range(stampWidth):
                    grid[i + x][j + y] = 0
            return dfs(i + 1, j) or dfs(i, j + 1)

        empty_cells = sum(row.count(0) for row in grid)
        if empty_cells == 0:
            return True
        for i in range(m):
            for j in range(n):
                if grid[i][j] == 0:
                    if dfs(i, j):
                        return True
        return False

    def isPossibleToCoverGrid(self, grid: list[list[int]], stampHeight: int, stampWidth: int) -> bool:
        return self.isPossibleToCover(grid, stampHeight, stampWidth)

