def evaluate(expression):
    def parse_expression(expression):
        tokens = expression.split()
        if len(tokens) == 1:
            return int(tokens[0])
        token = tokens[0]
        if token == 'let':
            vars = {}
            i = 1
            while i < len(tokens):
                var = tokens[i]
                i += 1
                expr = parse_expression(' '.join(tokens[i:i+2]))
                i += 2
                if var in vars:
                    raise ValueError("Variable already assigned")
                vars[var] = expr
            expr =''.join(tokens[i:])
            return evaluate(expr, vars)
        elif token == 'add':
            return parse_expression(' '.join(tokens[1:3])) + parse_expression(' '.join(tokens[3:]))
        elif token =='mult':
            return parse_expression(' '.join(tokens[1:3])) * parse_expression(' '.join(tokens[3:]))
        else:
            return int(token)

    def evaluate(expr, vars={}):
        if expr.isdigit():
            return int(expr)
        elif expr.startswith('let'):
            return parse_expression(expr)
        else:
            tokens = expr.split()
            if tokens[0] in vars:
                return vars[tokens[0]]
            else:
                return parse_expression(expr)

    return evaluate(expression)

