            if equations[i][0] not in graph:
                graph[equations[i][0]] = {}
            if equations[i][1] not in graph:
                graph[equations[i][1]] = {}

            # Step 4: Add the relationships between variables to the graph
            graph[equations[i][0]][equations[i][1]] = values[i]
            graph[equations[i][1]][equations[i][0]] = 1 / values[i]

        # Step 5: Initialize the result list
        result = []

        # Step 6: Process each query
        for query in queries:
            # Step 7: Check if the query is in the graph
            if query[0] not in graph or query[1] not in graph:
                result.append(-1.0)
                continue

            # Step 8: Perform a depth-first search to find the path between the variables in the query
            stack = [(query[0], 1.0)]
            visited = set()
            while stack:
                node, product = stack.pop()
                if node == query[1]:
                    result.append(product)
                    break
                for neighbor, value in graph[node].items():
                    if neighbor not in visited:
                        visited.add(neighbor)
                        stack.append((neighbor, product * value))
            else:
                result.append(-1.0)

        return result
