def largestGroupsOfStrings(words):
    def is_connected(word1, word2):
        return sum(c in word2 for c in word1) + sum(c in word1 for c in word2) - 2 * (word1[0] in word2 and word1[-1] in word2) == len(word1) + len(word2)

    def dfs(graph, node, visited, group_sizes):
