def minWastedSpace(packages, boxes):
    MOD = 10**9 + 7
    n = len(packages)
    m = len(boxes)
    packages.sort()  # Sort packages in ascending order
    pq = []
    for box in boxes:
        heapq.heappush(pq, (box[0], box[1]))

    ans = float('inf')
    max_box_size = 0
    for i in range(1, m + 1):
        max_box_size = max(max_box_size, pq[0][1])

    for i in range(m):
        max_box_size = max(max_box_size, pq[0][1])
        while pq and pq[0][1] == max_box_size:
            heapq.heappop(pq)

    if max_box_size < packages[-1]:
        return -1

    i = 0
    for package in packages:
        while i < n and packages[i] <= max_box_size:
            i += 1
        if i == n:
            break
        if pq:
            box_size, available = heapq.heappop(pq)
            if available > 0:
                heapq.heappush(pq, (box_size, available - 1))
            else:
                return -1
        else:
            return -1

    total_wasted_space = 0
    for package in packages:
        while i < n and packages[i] <= max_box_size:
            total_wasted_space += max_box_size - packages[i]
            i += 1

    for _ in range(i, n):
        total_wasted_space = (total_wasted_space * 2 + packages[_]) % MOD

    for box_size, available in pq:
        total_wasted_space = (total_wasted_space + (box_size * available)) % MOD

    return total_wasted_space % MOD
```

