```cpp
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class FindElements {
public:
    FindElements(TreeNode* root) {
        recover(root, 0);
    }

    bool find(int target) {
        return recovered.find(target)!= recovered.end();
    }

private:
    unordered_set<int> recovered;

    void recover(TreeNode* node, int index) {
        if (!node) return;

        int originalValue = calculateOriginalValue(index);
        node->val = originalValue;
        recovered.insert(originalValue);

        recover(node->left, 2 * index + 1);
        recover(node->right, 2 * index + 2);
    }

    int calculateOriginalValue(int index) {
        return index == 0? 0 : (index - 1) / 2;
    }
};
```

