class Solution:
    def deleteIdentical(self, paths):
        # Step 1: Function to check if two folders are identical
        def areIdentical(folder1, folder2, visited):
            if folder1 == folder2:
                return True
            if folder1 in visited or folder2 in visited:
                return False
            visited.add(folder1)
            visited.add(folder2)
            for i in range(min(len(folder1), len(folder2))):
                if not areIdentical(folder1[i], folder2[i], visited):
                    return False
            if len(folder1)!= len(folder2):
                return False
            return True

        # Step 2: Function to mark and delete identical folders and their subfolders
        def markAndDelete(paths):
            marked = set()
            for path in paths:
                self.dfs(path, marked, set())
            return [path for path in paths if path not in marked]

        # Step 3: Function to perform DFS
        def dfs(path, marked, visited):
            if path in marked or path in visited:
                return
            visited.add(path)
            for i in range(len(path)):
                if areIdentical(path[:i], path[:i+1], visited):
                    marked.add(path)
                    self.dfs(path[:i], marked, visited)
                    self.dfs(path[:i+1], marked, visited)

        # Step 4: Perform DFS on all paths and return the result
        return markAndDelete(paths)

