```python
def splitArraySameAverage(nums):
    def is_subset_sum(nums, target, i, path):
        if path:
            if sum(path) == target:
                return True
            if sum(path) > target or i == len(nums):
                return False
        for j in range(i, len(nums)):
            if is_subset_sum(nums, target, j + 1, path + [nums[j]]):
                return True
        return False

    total_sum = sum(nums)
    if total_sum % 2 == 0:
        return True
    else:
        for i in range(1, total_sum // 2):
            if is_subset_sum(nums, total_sum - i, 0, []):
                return True
        return False
