class Solution {
    public Node findSuccessor(Node node) {
        // Base case: If the node is null, return null
        if (node == null) {
            return null;
        }

        // If the node has a right child, the in-order successor is the smallest node in the right subtree
        if (node.right!= null) {
            // Move to the right child and find the smallest node (in-order successor)
            return findMin(node.right);
        }

        // If the node has no right child, we need to move up the tree to find the in-order successor
        // Start from the parent of the current node
        Node parent = node.parent;
        while (parent!= null) {
            // If the parent of the current node is the left child of its parent, the parent is the in-order successor
            if (parent.left == node) {
                return parent;
            }
            // Move up the tree to the parent's parent
            node = parent;
            parent = parent.parent;
        }

        // If we have reached the root of the tree and haven't found an in-order successor, return null
        return null;
    }

    // Helper function to find the smallest node in a given tree (in-order successor)
    private Node findMin(Node node) {
        while (node.left!= null) {
            node = node.left;
        }
        return node;
    }
}
