class Solution:
    def minOperations(self, n: int, p: int, banned: list[int]) -> list[int]:
        # Step 1: Create a set to store banned positions and the target position for efficient lookups
        banned_set = set(banned)
        target_set = {p}

        # Step 2: Initialize the result array with -1, assuming it's impossible to bring the 1 to each position
        ans = [-1] * n

        # Step 3: Initialize the distance to the target position to 0
        ans[p] = 0

        # Step 4: Iterate over all possible positions in the array
        for i in range(n):
            if i!= p:
                # Calculate the distance to the target position
                dist = min(abs(i - p), (n - abs(i - p)))

                # If the position is not banned and it's not the target position, update the result array
                if i not in banned_set and i!= p:
                    ans[i] = dist

        # Step 5: Iterate over all positions in the array to find the minimum number of reverse operations needed
        for i in range(n):
            if i not in banned_set:
                for j in range(i + 1, n):
                    # Check if the subarray [i, j] can be reversed to bring the 1 to position i
                    if j - i + 1 in target_set:
                        # Update the result array with the minimum number of reverse operations
                        ans[i] = min(ans[i], ans[j] + 1)

        return ans
```

