    def longestCycle(self, edges):
        n = len(edges)
        visited = [False] * n
        max_cycle_length = 0

        for i in range(n):
            if not visited[i]:
                max_cycle_length = max(max_cycle_length, self.dfs(i, edges, visited, -1))

