    def __init__(self):
        self.children = {}
        self.end_of_word = False


class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.end_of_word = True


class Solution:
    def findWords(self, board: list[list[str]], words: list[str]) -> list[str]:
        self.result = []
        self.trie = Trie()
        for word in words:
            self.trie.insert(word)

        rows, cols = len(board), len(board[0])
        self.visited = [[False] * cols for _ in range(rows)]

        for i in range(rows):
            for j in range(cols):
                self.dfs(board, i, j, "", self.trie.root, self.result)

        return self.result

    def dfs(self, board, i, j, path, node, result):
        if node.end_of_word:
            result.append(path)
            node.end_of_word = False

        if i < 0 or i >= len(board) or j < 0 or j >= len(board[0]) or self.visited[i][j]:
            return

        self.visited[i][j] = True
        for di, dj in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            ni, nj = i + di, j + dj
            if ni < 0 or ni >= len(board) or nj < 0 or nj >= len(board[0]):
                continue
            if board[i][j] not in node.children:
                continue
            self.dfs(board, ni, nj, path + board[i][j], node.children[board[i][j]], result)
        self.visited[i][j] = False


