class Solution:
    def calculateMinimumHP(self, dungeon: List[List[int]]) -> int:
        m, n = len(dungeon), len(dungeon[0])
        dp = [[float('inf')] * n for _ in range(m)]

        # Initialize the first row and column
        dp[0][0] = max(-dungeon[0][0], 1)

        for i in range(1, n):
            dp[0][i] = max(dp[0][i-1], -dungeon[0][i])

        for i in range(1, m):
            dp[i][0] = max(dp[i-1][0], -dungeon[i][0])

        for i in range(1, m):
            for j in range(1, n):
                dp[i][j] = min(max(dp[i-1][j], dp[i][j-1], -dungeon[i][j]), 1)

        return dp[m-1][n-1]
