class WordDictionary:
    def __init__(self):
        # Initialize the trie with a root node.
        self.root = TrieNode()

    def addWord(self, word: str) -> None:
        # Start at the root node and traverse the trie character by character.
        node = self.root
        for char in word:
            if char not in node.children:
                # If the character is not in the node's children, add a new node.
                node.children[char] = TrieNode()
            node = node.children[char]
        # Mark the end of the word.
        node.is_end_of_word = True

    def search(self, word: str) -> bool:
        # Start at the root node and traverse the trie character by character.
        return self._search_from(word, self.root)

    def _search_from(self, word: str, node: TrieNode) -> bool:
        # If the word is empty, return whether the current node represents the end of a word.
        if not word:
            return node.is_end_of_word
        # If the current character is a dot, match any character.
        if word[0] == '.':
            # Try matching any character in the node's children.
            for child in node.children.values():
                if self._search_from(word[1:], child):
                    return True
            return False
        # If the current character is not a dot, match the exact character.
        else:
            # If the character is not in the node's children, return False.
            if word[0] not in node.children:
                return False
            # Recursively search from the child node.
            return self._search_from(word[1:], node.children[word[0]])
```

