def secondMinimum(n: int, edges: List[List[int]], time: int, change: int) -> int:
    graph = [[] for _ in range(n + 1)]
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    visited = set()
    queue = deque([(1, 0, 0, 0)]  # vertex, time, color, second_min_time
    min_time = {1: (0, float('inf'))}
    second_min_time = {1: (0, float('inf'))}

    while queue:
        vertex, curr_time, curr_color, second_min_time_val = queue.popleft()
        if vertex == n:
            return second_min_time_val

        if vertex in visited:
            continue
        visited.add(vertex)

        for neighbor in graph[vertex]:
            new_time = curr_time + time
            new_color = (curr_color + 1) % 2
            if new_color == 0:
                new_time = curr_time + time
            else:
                new_time = curr_time + time + change

            if new_time < min_time[neighbor][0] or (new_time == min_time[neighbor][0] and new_time < second_min_time[neighbor][0]):
                second_min_time[neighbor] = min_time[neighbor]
                min_time[neighbor] = (new_time, second_min_time[neighbor][0])
                queue.append((neighbor, new_time, new_color, min_time[neighbor][0]))
            elif new_time < second_min_time[neighbor][0] or (new_time == second_min_time[neighbor][0] and new_time < second_min_time[neighbor][1]):
                second_min_time[neighbor] = (min_time[neighbor][0], new_time)
                queue.append((neighbor, new_time, new_color, second_min_time[neighbor][0]))

    return -1
