        int colDiff = col > 0? 1 : 0;
        int rowSum = row > 0? prefixSum[row - 1][col] : 0;
        int colSum = col > 0? prefixSum[row][col - 1] : 0;
        int cornerSum = row > 0 && col > 0? prefixSum[row - 1][col - 1] : 0;
        int newSum = val + (rowSum - cornerSum) + (colSum - cornerSum);
