class Solution:
    def numDecodings(self, s: str) -> int:
        MOD = 10**9 + 7
        if not s or s[0] == '0':
            return 0

        n = len(s)
        dp = [0] * (n + 1)
        dp[0] = 1
        dp[1] = 1

        for i in range(2, n + 1):
            if s[i - 1]!= '0':
                dp[i] += dp[i - 1]
            if '10' <= s[i - 2:i] <= '26':
                dp[i] += dp[i - 2]
            if s[i - 1] == '*':
                dp[i] += dp[i - 1]
                if i >= 2 and '10' <= s[i - 3:i] <= '26':
                    dp[i] += dp[i - 2]
            dp[i] %= MOD

        return dp[n]
```

