from typing import List

class Solution:
    def countDistinctPaths(self, vals: List[int], edges: List[List[int]], n: int) -> int:
        # Step 1: Create an adjacency list to represent the tree
        graph = [[] for _ in range(n)]
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        # Step 2: Perform DFS to find the maximum value node in each connected component
        max_val_nodes = set()
        visited = [False] * n
        for i in range(n):
            if not visited[i]:
                self.dfs(graph, i, vals, max_val_nodes, visited)

        # Step 3: Count the number of distinct good paths
        count = 0
        for max_val in max_val_nodes:
            count += self.count_good_paths(graph, vals, max_val)

        return count

    def dfs(self, graph: List[List[int]], node: int, vals: List[int], max_val_nodes: set, visited: List[bool]) -> None:
        visited[node] = True
        max_val = vals[node]
        for neighbor in graph[node]:
            if not visited[neighbor]:
                self.dfs(graph, neighbor, vals, max_val_nodes, visited)
                max_val = max(max_val, vals[neighbor])
        max_val_nodes.add(max_val)

    def count_good_paths(self, graph: List[List[int]], vals: List[int], max_val: int) -> int:
        count = 0
        visited = [False] * len(graph)
        stack = [(max_val, 1)]
        while stack:
            node, path_count = stack.pop()
            if not visited[node]:
                visited[node] = True
                for neighbor in graph[node]:
                    if vals[neighbor] <= node and not visited[neighbor]:
                        stack.append((neighbor, path_count + 1))
                if node == max_val:
                    count += path_count
        return count
```

