class TreeNode:
    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

class Solution:
    def removeLeafNodes(self, root: TreeNode, target: int) -> TreeNode:
        def helper(node):
            # Step 1: Base case 1 - If the current node is None, return None
            if not node:
                return None

            # Step 1: Base case 2 - If the current node is a leaf node and its value is target, return None
            if not node.left and not node.right and node.val == target:
                return None

            # Recursively call the helper function on the current node's left and right children
            node.left = helper(node.left)
            node.right = helper(node.right)

            # If the current node is a leaf node and its value is not target, return the node
            if not node.left and not node.right:
                return node

            # If the current node is not a leaf node, return the node
            return node

        return helper(root)
