class Excel:
    def __init__(self, height: int, width: str):
        self.height = height
        self.width = width
        self.width_int = self.colToInt(self.width)
        self.mat = [[0 for _ in range(self.width_int)] for _ in range(height)]

    def colToInt(self, col: str) -> int:
        return ord(col) - ord('A')

    def validate(self, numbers: list) -> list:
        for num in numbers:
            if num[0] == ':':
                num = num.split(':')
                start = self.colToInt(num[0][:-1])
                end = self.colToInt(num[1])
                row1 = int(num[0][-1])
                row2 = int(num[1])
                for row in range(row1, row2 + 1):
                    for col in range(start, end + 1):
                        numbers.append(f'{self.charToInt(col)}{row}')
            else:
                row = int(num[-1])
                col = self.colToInt(num[:-1])
                numbers.append(f'{self.charToInt(col)}{row}')
        return numbers

    def charToInt(self, col: str) -> str:
        return chr(ord(col) + ord('A') - 1)

    def set(self, row: int, column: str, val: int) -> None:
        col = self.colToInt(column)
        self.mat[row - 1][col] = val

    def get(self, row: int, column: str) -> int:
        col = self.colToInt(column)
        return self.mat[row - 1][col]

    def sum(self, row: int, column: str, numbers: list) -> int:
        numbers = self.validate(numbers)
        val = 0
        for num in numbers:
            col = self.colToInt(num[:-1])
            row_val = int(num[-1])
            val += self.mat[row_val - 1][col]
        self.set(row, column, val)
        return val

