def ways(pizza: list[list[str]], k: int) -> int:
    MOD = 10**9 + 7
    rows, cols = len(pizza), len(pizza[0])
    dp = [[0] * cols for _ in range(rows)]

    # Base case: Initialize the first row and column
    for j in range(cols):
        if pizza[0][j] == 'A':
            dp[0][j] = 1
    for i in range(rows):
        if pizza[i][0] == 'A':
            dp[i][0] = 1

    # Fill the dp table in a bottom-up manner
    for i in range(1, rows):
        for j in range(1, cols):
            if pizza[i][j] == 'A':
                dp[i][j] = (dp[i-1][j] + dp[i][j-1] + dp[i-1][j-1]) % MOD

    # Calculate the total number of ways to cut the pizza
    total_ways = dp[rows-1][cols-1]

    # Calculate the number of ways to cut the pizza such that the last piece contains at least one apple
    last_piece_ways = 0
    for i in range(rows):
        for j in range(cols):
            if pizza[i][j] == 'A':
                last_piece_ways = (last_piece_ways + dp[i][j]) % MOD

    # Calculate the final answer
    return (total_ways - last_piece_ways + MOD) % MOD
```
