```python
class Solution:
    def calculate(self, s: str) -> int:
        def apply_operator(operators, numbers):
            result = numbers.pop()
            while operators and operators[-1]!= '(':
                op = operators.pop()
                if op == '*':
                    result = numbers.pop() * result
                elif op == '/':
                    result = int(result / numbers.pop())
                else:
                    result = numbers.pop() + result if op == '+' else result - numbers.pop()
            return result

        numbers = []
        operators = []
        i = 0
        while i < len(s):
            # Extract numbers
            while i < len(s) and s[i].isdigit():
                j = i
                while i < len(s) and s[i].isdigit():
                    i += 1
                numbers.append(int(s[j:i]))
                i += 1

            # Extract operators
            if i < len(s):
                if s[i] == '(':
                    operators.append(s[i])
                    i += 1
                elif s[i] == ')':
                    while operators and operators[-1]!= '(':
                        numbers.append(apply_operator(operators, numbers))
                    operators.pop()  # Remove the '('
                    i += 1
                else:
                    operators.append(s[i])
                    i += 1

            # Apply operators from left to right
            while operators and numbers:
                numbers.append(apply_operator(operators, numbers))

        # Apply the remaining operators
        while operators:
            numbers.append(apply_operator(operators, numbers))

        return numbers[0]

# Example usage
