class Solution:
    def minMalwareSpread(self, graph: list[list[int]], initial: list[int]) -> int:
        n = len(graph)
        initial_set = set(initial)
        visited = set()
        infect_set = set()
        infect_count = [0] * n
        infect_count2 = [0] * n

        def dfs(node: int, parent: int) -> None:
            if node in visited:
                return
            visited.add(node)
            infect_count[node] = 1
            for neighbor in range(n):
                if graph[node][neighbor] == 1 and neighbor!= parent:
                    dfs(neighbor, node)
                    infect_count[node] += infect_count[neighbor]
                    if neighbor in initial_set:
                        infect_set.add(node)

        for node in range(n):
            if node not in visited:
                dfs(node, -1)

        infect_count2 = [i for i in infect_count if i!= 0]
        max_infect = max(infect_count2)
        max_infect_nodes = [i for i, count in enumerate(infect_count) if count == max_infect]

        for node in initial_set:
            if infect_count[node] == max_infect:
                infect_set.remove(node)

        return min(max_infect_nodes)
```

