import heapq
from collections import defaultdict

class TrieNode:
    def __init__(self):
        self.children = defaultdict(TrieNode)
        self.frequency = 0
        self.sentence = ""

class AutocompleteSystem:
    def __init__(self, sentences: list[str], times: list[int]):
        self.root = TrieNode()
        self.map = {}
        for sentence, time in zip(sentences, times):
            node = self.root
            for char in sentence:
                node = node.children[char]
            node.frequency = time
            node.sentence = sentence
            self.map[tuple(node.children.keys())] = node

    def input(self, c: str) -> list[str]:
        if c == '#':
            return []
        node = self.root
        for char in c:
            if char not in node.children:
                return []
            node = node.children[char]
        queue = []
        self._dfs(node, queue)
        if len(queue) < 3:
            return [node.sentence for node in queue]
        return [node.sentence for node in heapq.nsmallest(3, queue, key=lambda node: (node.frequency, node.sentence))]

    def _dfs(self, node, queue):
        if node.frequency > 0:
            queue.append(node)
        for child in node.children.values():
            self._dfs(child, queue)
