from collections import defaultdict

def findRedundantDirectedConnection(edges):
    n = len(edges)
    parent = [-1] * (n + 1)
    children = [0] * (n + 1)

    # Step 1: Construct an adjacency list representation of the graph
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        children[v] += 1

    # Step 2: Find the root node and its children
    root = -1
    for i in range(1, n + 1):
        if parent[i] == -1:
            root = i
            break

    # Step 3: Perform DFS to find the redundant edge
    def dfs(node, parent):
        for child in graph[node]:
            if parent!= child and children[child] == 0:
                children[parent] -= 1
                if dfs(child, node):
                    return True
            elif parent!= child and children[child] == 1:
                return (node, child)
        return False

    # Step 4: If the graph is a tree, return the last edge
    if root!= -1 and dfs(root, -1):
        return edges[-1]

    # Step 5: If the graph is not a tree, return the edge that makes it a tree
    for i in range(1, n + 1):
        if children[i] == 0:
            for j in graph[i]:
                if children[j] > 0:
                    return [i, j]

    return []

