    n = len(parent)
    node_chars = {i: c for i, c in enumerate(s)}
    parent_map = {i: p for i, p in enumerate(parent)}

    def dfs(node):
        if node in dp:
            return dp[node]

        max_len = 0
        for child in [i for i in range(n) if parent_map[i] == node]:
            max_len = max(max_len, dfs(child))

        dp[node] = 1 + max_len
        for child in [i for i in range(n) if parent_map[i] == node]:
            if node_chars[child] == node_chars[node]:
                dp[node] = max(dp[node], dfs(child) + 1)

        return dp[node]

    dp = {}
    return max(dfs(i) for i in range(n) if i!= 0)

