def expressionToWords(expression):
    def helper(expression):
        if expression[0] == '{':
            # If the expression is a list of expressions, find the union of the results
            expressions = expression[1:-1].split('},')
            expressions = [e.strip() for e in expressions]
            return set(''.join(words) for words in itertools.product(*[helper(e) for e in expressions]))
        elif '+' in expression:
