def longestCommonSubpath(n, paths):
    def get_unique_substrings(path):
        unique_substrings = set()
        for window_size in range(1, len(path) + 1):
            for i in range(len(path) - window_size + 1):
                substring = tuple(path[i:i + window_size])
                unique_substrings.add(substring)
        return unique_substrings

    def check_common_substring(substring):
        for path in paths:
            if substring not in set(tuple(path[i:i + len(substring)]) for i in range(len(path) - len(substring) + 1)):
                return False
        return True

