            return memo[(i, target)]

        if i == n:
            return 0 if target == 0 else -float('inf')

        if target == 0:
            return 0

        include = dfs(i + 1, target - rods[i], n, memo) + rods[i]
        exclude = dfs(i + 1, target, n, memo)

        memo[(i, target)] = max(include, exclude)
        return memo[(i, target)]

    n = len(r)
    total_sum = sum(r)
    if total_sum % 2!= 0:
        return 0

    target = total_sum // 2
    memo = {}
    return dfs(0, target, n, memo)

def maxEqualSumOfAnySubarray(r: List[int]) -> float:
    n = len(r)
    total_sum = sum(r)
    if total_sum % 2!= 0:
        return 0

    target = total_sum // 2
    memo = {}
    return largestSumOfAverages(r, 2)

