def findAllPeople(n, meetings, firstPerson):
    # Step 1: Sort the meetings in ascending order based on their time
    meetings.sort(key=lambda x: x[2])

    # Step 2: Create a dictionary to store the set of people who have the secret at each time point
    people_with_secret_at_time = {0: {0, firstPerson}}

    # Step 3: Iterate over each meeting
    for time, person1, person2 in meetings:
        # Update the set of people with the secret at the current time point
        people_with_secret_at_time[time] = people_with_secret_at_time.get(time - 1, set()) | people_with_secret_at_time.get(time - 1, set()) | {person1, person2}

    # Step 4: Find the people who have the secret at the last time point
    people_with_secret = set()
    for time in sorted(people_with_secret_at_time.keys()):
        people_with_secret |= people_with_secret_at_time[time]

    return list(people_with_secret)
