def hasValidPath(grid):
    m, n = len(grid), len(grid[0])

    def dfs(i, j):
        # Base case: If the current cell is out of bounds or it's a dead end, return False
        if i < 0 or j < 0 or i >= m or j >= n or grid[i][j] == 0:
            return False
        # If the current cell is the target cell, return True
        if i == m - 1 and j == n - 1:
            return True
        # Mark the current cell as visited
        grid[i][j] = 0
        # Check the valid streets to move to the next cell
        if grid[i][j] in [1, 5]:
            # If the current cell has a street to the right, move to the right
            if dfs(i, j + 1):
                return True
        if grid[i][j] in [1, 6]:
            # If the current cell has a street to the upper cell, move to the upper cell
            if dfs(i - 1, j):
                return True
        if grid[i][j] in [2, 4]:
            # If the current cell has a street to the lower cell, move to the lower cell
            if dfs(i + 1, j):
                return True
        if grid[i][j] in [2, 6]:
            # If the current cell has a street to the left, move to the left
            if dfs(i, j - 1):
                return True
        # If no valid move is found, return False
        return False

    return dfs(0, 0)
